import React, { useState } from 'react';
import './login.css'

function Register() {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    resume: null,
  });

  const handleInputChange = (e) => {
    const { name, value, type, files } = e.target;
    const updatedData = type === 'file' ? { [name]: files[0] } : { [name]: value };
    setFormData({ ...formData, ...updatedData });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here, you can send the form data (including the resume file) to the server for processing.
    // This example does not include the server-side code.
    console.log(formData);
  };

  return (
    <div className='login-container'>
      <div className=''>

      </div>
      <h1 className='login-header'>Job Seeker Signup</h1>
      <form onSubmit={handleSubmit}>
        <label>
          First Name:
          <input type="text" name="firstName" value={formData.firstName} onChange={handleInputChange} />
        </label>
        <br />

        <label>
          Last Name:
          <input type="text" name="lastName" value={formData.lastName} onChange={handleInputChange} />
        </label>
        <br />

        <label>
          Email:
          <input type="email" name="email" value={formData.email} onChange={handleInputChange} />
        </label>
        <br />

        <label>
          Password:
          <input type="password" name="password" value={formData.password} onChange={handleInputChange} />
        </label>
        <br />

        <label>
          Resume (PDF or DOCX):
          <input type="file" name="resume" accept=".pdf,.docx" onChange={handleInputChange} />
        </label>
        <br />

        <button type="submit">Sign Up</button>
      </form>
    </div>
  );
}

export default Register;
