import React, { useState } from 'react';

function RecruiterRegister() {
  const [formData, setFormData] = useState({
    recruiterName: '',
    companyName: '',
    companyDescription: '',
    address: '',
    phoneNumber: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here, you can send the form data to the server for processing.
    // This example does not include the server-side code.
    console.log(formData);
  };

  return (
    <div>
      <h1>Recruiter Signup</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Recruiter Name:
          <input type="text" name="recruiterName" value={formData.recruiterName} onChange={handleInputChange} />
        </label>
        <br />

        <label>
          Address:
          <input type="text" name="address" value={formData.address} onChange={handleInputChange} />
        </label>
        <br />

        <label>
          Phone Number:
          <input type="tel" name="phoneNumber" value={formData.phoneNumber} onChange={handleInputChange} />
        </label>
        <br />

        <label>
          Company Name:
          <input type="text" name="companyName" value={formData.companyName} onChange={handleInputChange} />
        </label>
        <br />

        <label>
          Company Description:
          <textarea name="companyDescription" value={formData.companyDescription} onChange={handleInputChange} />
        </label>
        <br />



        <button type="submit">Sign Up</button>
      </form>
    </div>
  );
}

export default RecruiterRegister;
